let Declaration = require('../declaration')

class MaskBorder extends Declaration {
  /**
   * Return property name by final spec
   */
  normalize() {
    return this.name.replace('box-image', 'border')
  }

  /**
   * Return flex property for 2012 spec
   */
  prefixed(prop, prefix) {
    let result = super.prefixed(prop, prefix)
    if (prefix === '-webkit-') {
      result = result.replace('border', 'box-image')
    }
    return result
  }
}

MaskBorder.names = [
  'ma***REMOVED***border',
  'ma***REMOVED***border-source',
  'ma***REMOVED***border-slice',
  'ma***REMOVED***border-width',
  'ma***REMOVED***border-outset',
  'ma***REMOVED***border-repeat',
  'ma***REMOVED***box-image',
  'ma***REMOVED***box-image-source',
  'ma***REMOVED***box-image-slice',
  'ma***REMOVED***box-image-width',
  'ma***REMOVED***box-image-outset',
  'ma***REMOVED***box-image-repeat'
]

module.exports = MaskBorder
